import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props)

        this.state = {
            nameString: "",
        }
    }

    handleNameChange = (event) =>{
        this.setState({
            email : event.target.value
        })
    }

    render() {
        return (
            <form>
                <div>
                    <label>Email</label>
                    <input type='text' onChange = {this.handleNameChange}></input></div>
                <div><button type = "Submit">Change to Uppercase</button></div>
        {/* <div>{}</div> */}
            </form>
        )
    }
}

export default Form
