import React, { Component } from 'react'

class Message extends Component {
    constructor(props) {
        super(props)

        this.state = {
             text: 'Welcome Visitor'
        }
    }
    handleText()
    {
        this.setState({
            text:"Thanks For Subscribing"
        })
    }
    render() {
        return (
            <div>
               {this.state.text}
               <button onClick = {()=> this.handleText}>Subscribe</button>
            </div>
        )
    }
}

export default Message
