import React, { Component } from 'react'

class Counter extends Component {

    constructor(props) {
        super(props)

        this.state = {
             count: 0
        }
    }

    handleCounter()
    {
        // this.setState({
        //     count : this.state.count  + 1
        // }, ()=> {
        //     console.log(this.state.count)
        // })

        this.setState ((preState) =>({
            count : preState.count + 1
        }))
    }

    handleCounterFive()
    {
        this.handleCounter()
        this.handleCounter()
        this.handleCounter()
        this.handleCounter()
        this.handleCounter()
    }

    render() {
        return (
            <div>
             <div>
                count - {this.state.count}
            </div>
            <button onClick = {()=>this.handleCounterFive()}>
                Increase Value
            </button>
            </div>

        )
    }
}

export default Counter
